export async function connectRedis() {
  if (!redis) {
    redis = createClient({
      url: "redis://localhost:6379",
      socket: {
        connectTimeout: 10000,
      },
    });
    redis.on("ready", () => {
      console.log("Redis client connected and ready");
    });
    redis.on("error", (err) => {
      console.error("Redis error:", err);
    });

    // Mantém a conexão viva com ping periódico
    setInterval(() => {
      redis?.ping();
    }, 10000); // Pinga a cada 10 segundos
    await redis.connect();
  }

  async function getLastVersion(owner: string, repo: string) {
    const versions = (await redis.json.get(`version:${owner}:${repo}`, {
      path: "$",
    })) as {
      [commitHash: string]: {
        timestamp: number;
        status?: "analysing" | "analysed";
      };
    };

    const [lastestVersion] = Object.entries(versions)
      .filter(([, value]) => value.status === "analysed")
      .map(([hash, value]) => ({
        hash,
        timestamp: value.timestamp,
      }))
      .sort((a, b) => a.timestamp - b.timestamp)
      .reverse();

    return lastestVersion.hash;
  }

  async function getItemsByKeyPattern<T>(pattern: string) {
    const data: T[] = [];
    let cursor = 0;

    do {
      const result = await redis.scan(cursor, {
        MATCH: pattern,
        COUNT: 1000,
      });

      cursor = result.cursor;
      const keys = result.keys;

      for (const key of keys) {
        const response = await redis.json.get(key);
        data.push({ key, data: response } as T);
      }
    } while (cursor !== 0);

    return data;
  }

  return {
    getLastVersion,
    getItemsByKeyPattern,
  };
}

type ResponseData = {
  data: {
    installations: {
      [id: string]: {
        id: number;
        repository_selection: string;
        created_at: Date;
        updated_at: Date;
      };
    };
    repositories: {
      [id: string]: {
        id: number;
        name: string;
        owner: string;
        is_batch_api: boolean;
      };
    };
  };
};

installationWorker = createWorker<InstallationWorkerData>(
  "installation",
  async (job, token) => {
    try {
      const { repositories, type, step = "validating" } = job.data;

      switch (type) {
        case "installation": {
          switch (step) {
            case "validating": {
              console.log(job.name, " start with data ", job.data);
              const git = await createSimpleGit({});
              const installationToken = generateJWT();

              for (const repo of repositories) {
                const repoUrl = `https://x-access-token:${installationToken}@github.com/${repo.owner}/${repo.name}.git`;

                const commitHash = await git.clone(repoUrl);

                console.log(job.name, " repo ", repo, " cloned");
                await analyseRepositoryQueue.add(
                  "repo-cloned",
                  {
                    commitHash: commitHash,
                    name: repo.name,
                    owner: repo.owner,
                  },
                  {
                    parent: {
                      id: job.id!,
                      queue: job.queueQualifiedName,
                    },
                  }
                );
              }

              await job.updateData({ ...job.data, step: "in_progress" });
              await job.moveToDelayed(Date.now() + 10, token);
              throw new DelayedError();
            }
            case "in_progress": {
              console.log(job.name, "wait children");
              const shouldWait = await job.moveToWaitingChildren(token!);
              if (shouldWait) throw new WaitingChildrenError();

              await job.updateData({ ...job.data, step: "completed" });
              await job.moveToDelayed(Date.now() + 10, token);
              throw new DelayedError();
            }
            default: {
              console.log(job.name, " installation completed");
              break;
            }
          }

          break;
        }
        case "uninstallation": {
          break;
        }
        default: {
          break;
        }
      }
    } catch (error) {
      console.error({ error });
      throw error;
    }
  }
)

defaultBranchChangeWorker = createWorker<BranchChangeWorkerData>(
  "branch-change",
  async (job) => {
    try {
      const { name, owner } = job.data.repo;

      const redis = await connectRedis();
      const lastCommitHash = await redis.getLastVersion(owner, name);

      const git = await createSimpleGit({});
      const commitHash = await git.pull(lastCommitHash);

      await analyseRepositoryQueue.add("branch-updated", {
        name,
        owner,
        commitHash,
        previousCommitHash: lastCommitHash,
      });
    } catch (error) {
      console.error({ error });
      // throw error;
    }
  }
)

analyseRepositoryWorker =
  createWorker<AnalyseRepositoryWorkerData>(
    "analyse-repository",
    async (job, token) => {
      const { commitHash, previousCommitHash } = job.data;
      if (!commitHash) return;

      const workingDirectory = path.resolve(
        process.cwd(),
        "./data/repos",
        commitHash
      );

      const git = await createSimpleGit({});

      switch (job.data.step || "initial") {
        case "initial": {
          console.log(job.name, " starting with data ", job.data);
          const { tsFiles } = await git.getFilesDiff(
            commitHash,
            previousCommitHash || git.firstCommit
          );

          console.log(job.name, " analysing ", tsFiles.length, " files");
          const blocks = tsFiles.map((data) => {
            const analyse = {
              ...data,
              analyse: createTypescriptAnalyser(
                workingDirectory,
                data.filePath
              ).analyze(),
            };
            return analyse;
          });
          console.log(
            job.name,
            " extracted ",
            blocks.reduce((acc, file) => acc + file.analyse.blocks.length, 0),
            " blocks"
          );

          //cria jobs filhas para criação das descrições com a IA e aguarda conclusão
          await analyseFileQueue.addBulk(
            blocks.map((block) => {
              return {
                name: `${path.basename(block.filePath)}`,
                data: {
                  commitHash,
                  previousCommitHash: previousCommitHash || git.firstCommit,
                  fileAnalysis: { ...block.analyse },
                  filePath: block.filePath,
                  status: block.status,
                },
                opts: {
                  parent: {
                    id: job.id!,
                    queue: job.queueQualifiedName,
                  },
                },
              };
            })
          );

          await job.updateData({ ...job.data, step: "description-collect" });
          await job.moveToDelayed(Date.now() + 10, token);
          throw new DelayedError();
        }
        case "description-collect": {
          console.log(job.name, "wait children");
          const shoudWait = await job.moveToWaitingChildren(token!);
          if (shoudWait) throw new WaitingChildrenError();
          await job.updateData({ ...job.data, step: "completed" });

          console.log("descrições coletadas");
          await job.moveToDelayed(Date.now() + 10, token!);
          throw new DelayedError();
        }
        default:
          console.log(job.name, "analyseRepository completed!");
          break;
      }
      return;
    }
  )

analyseFileWorker = createWorker<AnalyseFileWorkerData>(
  "analyse-file",
  async (job, token) => {
    const {
      commitHash,
      status,
      filePath,
      fileAnalysis,
      documents = [],
      step = "validating",
    } = job.data;
    const { blocks } = fileAnalysis;

    if (status === "D") return; // se arquivo foi deleteado ignora-o

    // await chromaClient.deleteCollection({ name: "blocks" });
    const collection = await chromaClient.getOrCreateCollection({
      name: "blocks",
      embeddingFunction: new DefaultEmbeddingFunction(),
    });

    switch (step) {
      case "validating": {
        console.log(job.name, "starting with data", job.data);
        const data = await Promise.all(
          blocks.map(async (block) => {
            return collection.get({
              include: [
                "embeddings",
                "documents",
                "metadatas",
              ] as IncludeEnum[],
              where: {
                blockHash: block.blockHash,
              },
            });
          })
        );

        const docs = data
          .filter((block) => block.ids.length >= 1)
          .map((block) => ({
            id: block.ids[0],
            document: block.documents[0],
            embedding: block.embeddings?.[0],
            metadata: block.metadatas[0],
          }));

        console.log(job.name, " existing data ", docs.length);

        const undocumentedBlock = blocks.filter(
          (block) =>
            !docs.some((doc) => doc.metadata?.blockHash === block.blockHash)
        );

        console.log(job.name, " newdata data ", undocumentedBlock.length);

        if (undocumentedBlock.length > 0) {
          await chatCompletionQueue.add(
            path.basename(filePath),
            {
              messages: undocumentedBlock.map((block) => ({
                id: block.blockHash,
                content: block.source,
                type: "analyse",
              })),
            },
            {
              parent: {
                id: job.id!,
                queue: job.queueQualifiedName,
              },
            }
          );
        }

        await job.updateData({
          ...job.data,
          step: "in_progress",
          documents: [...documents, ...docs],
        });

        await job.moveToDelayed(Date.now() + 10, token);
        throw new DelayedError();
      }
      case "in_progress": {
        console.log(job.name, " waiting children");
        const shouldWait = await job.moveToWaitingChildren(token!);
        if (shouldWait) throw new WaitingChildrenError();

        const childrenResult = await job.getChildrenValues();
        const promptResults:
          | { id: string; result: string; usage: number }[]
          | undefined = Object.entries(childrenResult).map(
          ([, result]) => result
        )[0];

        try {
          //add undocumentedBlock
          if (promptResults?.length) {
            console.log(
              job.name,
              " add new ",
              promptResults.length,
              " blocks into chroma"
            );
            const doc = promptResults.reduce(
              (acc, cur) => ({
                ids: [...(acc.ids || []), randomUUID()],
                documents: [...(acc.documents || []), cur.result || ""],
                metadatas: [
                  ...(Array.isArray(acc.metadatas) ? acc.metadatas : []),
                  {
                    blockHash: cur.id,
                    commitHash,
                  },
                ],
              }),
              {} as AddRecordsParams
            );
            await collection.add(doc);
          }

          //replicate documentedDoc
          if (documents.length) {
            console.log(
              job.name,
              " replicate ",
              documents.filter((doc) => doc.metadata?.commitHash !== commitHash)
                .length,
              " blocks into chroma"
            );
            const doc = documents
              .filter((doc) => doc.metadata?.commitHash !== commitHash)
              .reduce(
                (acc, cur) => ({
                  ids: [...(acc.ids || []), randomUUID()],
                  documents: [...(acc.documents || []), cur.document || ""],
                  embeddings: [
                    ...(Array.isArray(acc.embeddings) ? acc.embeddings : []),
                    cur.embedding || [],
                  ] as Embeddings,
                  metadata: [
                    ...(Array.isArray(acc.metadatas) ? acc.metadatas : []),
                    {
                      ...cur.metadata,
                      commitHash,
                    },
                  ],
                }),
                {} as AddRecordsParams
              );
            await collection.add(doc);
          }
        } catch (error) {
          console.error({ error });
        }

        await job.updateData({
          ...job.data,
          step: "completed",
        });
        await job.moveToDelayed(Date.now() + 10, token);
        throw new DelayedError();
      }
      default:
        console.log("analiseFile completed!");
        break;
    }
  }
)

chatCompletionWorker = createWorker<
  ChatCompletionWorkerData,
  ChatCompletionWorkerResult[]
>("chat-completion", async (job, token) => {
  const { messages = [] } = job.data;
  const {
    step = "validating",
    attempt = 0,
    batch_id,
  } = job.data.progress || {};

  if (attempt >= MAX_ATTEMPT)
    throw new Error("Numero maximo de tentativas atingida.");

  switch (step) {
    case "validating": {
      const prompts = messages.map((msg) => ({
        id: msg.id,
        prompt: openai.preparePrompt({
          message: msg.content,
          type: msg.type,
        }),
      }));

      const batchPrompts = prompts.map((prompt) =>
        openai.prepareBatch({
          custom_id: prompt.id,
          body: prompt.prompt,
        })
      );
      const batchFile = await openai.createFile(
        batchPrompts.map((p) => JSON.stringify(p)).join("\n")
      );
      const batch = await openai.createBatch(batchFile.id);

      await job.updateData({
        ...job.data,
        progress: {
          ...job.data.progress,
          step: "in_progress",
          batch_id: batch.id,
        },
      });

      await job.moveToDelayed(Date.now() + 10, token);
      throw new DelayedError();
    }
    case "in_progress": {
      let delay = 100;
      try {
        const response: BatchProcessResponse[] = await openai.retrieveBatch(
          batch_id!
        );
        await job.updateData({
          ...job.data,
          progress: {
            ...job.data.progress,
            response: response.map((resp) => ({
              id: resp.custom_id,
              result: resp.response.body.choices[0].message.content,
              usage: resp.response.body.usage.total_tokens,
            })),
            step: "completed",
          },
        });
      } catch (error) {
        console.error({ error });
        await job.updateData({
          ...job.data,
          progress: { ...job.data.progress, attempt: attempt + 1 },
        });
        delay = Math.min(
          INITIAL_DELAY * Math.pow(BACKOFF_FACTOR, attempt),
          MAX_DELAY
        );
      }

      console.log("chatCompletion delay", delay);
      await job.moveToDelayed(Date.now() + delay, token);
      throw new DelayedError();
    }
    default:
      console.log("chatCompletion completed!");
      return job.data.progress?.response || [];
  }
})

