{ handlers, signIn, signOut, auth } = NextAuth({
  providers: [
    GitHub({
      account(account) {
        const refresh_token_expires_at =
          Math.floor(Date.now() / 1000) +
          Number(account.refresh_token_expires_in);
        return {
          access_token: account.access_token,
          expires_at: account.expires_at,
          refresh_token: account.refresh_token,
          refresh_token_expires_at,
        };
      },
      authorization: {
        params: { scope: "read:user repo read:org" },
      },
    }),
  ],
  session: {
    strategy: "jwt",
  },
  callbacks: {
    // async authorized({ auth }) {
    //   return !!auth;
    // },
    async jwt({ token, account, profile }) {
      if (account) {
        try {
          if (Date.now() >= token.expires_at * 1000) {
            const response = await fetch(
              "https://github.com/login/oauth/access_token",
              {
                method: "POST",
                body: new URLSearchParams({
                  client_id: process.env.AUTH_GITHUB_ID!,
                  client_secret: process.env.AUTH_GITHUB_SECRET!,
                  grant_type: "refresh_token",
                  refresh_token: token.refresh_token!,
                }),
              }
            );

            const tokensOrError = await response.json();

            if (!response.ok) throw tokensOrError;

            const newTokens = tokensOrError as {
              access_token: string;
              expires_in: number;
              refresh_token?: string;
            };

            token.access_token = newTokens.access_token;
            token.expires_at = Math.floor(
              Date.now() / 1000 + newTokens.expires_in
            );
            if (newTokens.refresh_token)
              token.refresh_token = newTokens.refresh_token;
          }

          token.username = `${profile?.login || ""}`;
          token.access_token = account.access_token!;
          token.expires_at = account.expires_at!;
          token.refresh_token = account.refresh_token;

          return {
            ...token,
          };
        } catch (error) {
          console.error("Error refreshing access_token", error);
          token.error = "RefreshTokenError";
          return { ...token };
        }
      } else {
        return { ...token };
      }
    },
    async session({ session, token }) {
      session.user.access_token = token.access_token;
      session.user.username = token.username;
      session.error = token.error;
      return session;
    },
  },
})

export async function connectRedis() {
  if (!redis) {
    redis = createClient({
      url: "redis://localhost:6379",
      socket: {
        connectTimeout: 10000,
      },
    });
    redis.on("ready", () => {
      console.log("Redis client connected and ready");
    });
    redis.on("error", (err) => {
      console.error("Redis error:", err);
    });

    // Mantém a conexão viva com ping periódico
    setInterval(() => {
      redis?.ping();
    }, 10000); // Pinga a cada 10 segundos
    await redis.connect();
  }
  return redis;
}

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

export function DashboardFilters({
  orgs,
}: {
  orgs: {
    id: number;
    name: string;
    url: string;
  }[];
}) {
  const pathname = usePathname();
  const router = useRouter();
  const searchParams = useSearchParams();
  const { data: session } = useSession();
  const owner = searchParams.get("owner") || session?.user.username;

  function handleFilterChange(value: string) {
    const params = new URLSearchParams(searchParams);
    if (value === session?.user.username) {
      params.delete("owner");
    } else {
      params.set("owner", value);
    }

    router.replace(`${pathname}?${params.toString()}`);
  }

  return (
    <div className="flex justify-center items-center gap-2">
      <DropdownMenu>
        <DropdownMenuTrigger asChild>
          <Button variant="outline" size="sm" className="gap-2">
            <ListFilter className="h-3.5 w-3.5" />
            <span className="sr-only sm:not-sr-only sm:whitespace-nowrap">
              Filtro
            </span>
          </Button>
        </DropdownMenuTrigger>
        <DropdownMenuContent align="end">
          <DropdownMenuLabel>Filtrar por repositórios</DropdownMenuLabel>
          <DropdownMenuSeparator />
          <DropdownMenuRadioGroup
            value={owner}
            onValueChange={handleFilterChange}
          >
            <DropdownMenuRadioItem value={session?.user.username || ""}>
              {session?.user.username}
            </DropdownMenuRadioItem>
            {orgs.map((org) => (
              <DropdownMenuRadioItem key={org.name} value={org.name}>
                {org.name}
              </DropdownMenuRadioItem>
            ))}
          </DropdownMenuRadioGroup>
        </DropdownMenuContent>
      </DropdownMenu>

      <SearcInput />
    </div>
  );
}

