export async function getRepository(
  access_token: string,
  owner: string,
  repo: string
) {
  const octokit = new Octokit({
    auth: access_token,
  });

  const fetched = await octokit.rest.repos.get({
    owner,
    repo,
  });

  return {
    id: fetched.data.id,
    name: fetched.data.name,
    description: fetched.data.description,
    owner: fetched.data.owner.login,
    created_at: fetched.data.created_at,
    updated_at: fetched.data.updated_at,
  };
}

export async function getRepositoryConfigAction(
  owner: string,
  id: number
): Promise<RepositoryConfig | null> {
  const redis = await connectRedis();
  const redisData = await redis.json.get(`user:${owner}`, {
    path: `$.repositories['${id}']`,
  });
  const config = redisData as RepositoryConfig;

  if (!config) return null;

  const reponseData = {
    ...config,
    api_token: config.api_token ? await decrypt(config.api_token) : undefined,
  };

  return reponseData as RepositoryConfig;
}

