function useToast() {
  const [state, setState] = React.useState<State>(memoryState);

  React.useEffect(() => {
    listeners.push(setState);
    return () => {
      const index = listeners.indexOf(setState);
      if (index > -1) {
        listeners.splice(index, 1);
      }
    };
  }, [state]);

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) =>
      dispatch({ type: actionTypes.DISMISS_TOAST, toastId }),
  };
}

repositoryConfigSchema = z.object({
  id: z.number(),
  name: z.string(),
  owner: z.string(),
  api_token: z.string().min(1, {
    message: "O token da API é obrigatório.",
  }),
  is_batch_api: z.boolean(),
})

export async function saveRepositoryConfigAction(
  id: number,
  owner: string,
  values: RepositoryConfig
) {
  const parse = repositoryConfigSchema.safeParse(values);

  if (parse.success) {
    const redis = await connectRedis();
    await redis.json.merge(`user:${owner}`, `$`, {
      repositories: {
        [id]: {
          ...parse.data,
          api_token: await encrypt(parse.data.api_token),
        },
      },
    });
  }
}

